<?php\\nheader(\\\'Content-Type: application/json\\\');\\n\\nrequire_once \\\'conexion.php\\\';\\n\\n// Disable display_errors and enable error_log for production\\nini_set(\\\'display_errors\\\', 0);\\nini_set(\\\'log_errors\\\', 1);\\nini_set(\\\'error_log\\\', __DIR__ . \\\'/php-error.log\\\');\\n\\n\\$response = [\\\'success\\\' => false, \\\'message\\\' => \\\'\\\', \\\'data\\\' => [\\\'totalClientes\\\' => 0]];\\n\\ntry {\\n    // Check if the database connection is established\\n    if (\\$conn === null) {\\n        throw new Exception(\\\'Database connection failed.\\\');\\n    }\\n\\n    // Query to get the total count of customers\\n    // Assuming your users table contains clients, and you can identify them by tipoUsuario\\n    // Adjust the query based on your actual table structure and how you identify clients\\n    // For example, if clients have tipoUsuario = \\\'cliente\\\'\\n    // \\$sql = \\\"SELECT COUNT(*) as totalClientes FROM usuarios WHERE tipoUsuario = \\\'cliente\\\'\\\";\\n    // If all users are considered clients for this stat:\\n     \\$sql = \\\"SELECT COUNT(*) as totalClientes FROM usuarios\\\";\\n    \\$stmt = \\$conn->prepare(\\$sql);\\n    \\$stmt->execute();\\n\\n    \\$result = \\$stmt->fetch(PDO::FETCH_ASSOC);\\n\\n    if (\\$result) {\\n        \\$response[\\\'success\\\'] = true;\\n        // Ensure the count is treated as an integer\\n        \\$response[\\\'data\\\'][\\\'totalClientes\\\'] = (int)\$result[\\\'totalClientes\\\'];\\n    } else {\\n         throw new Exception(\\\'Could not fetch customer count.\\\');\\n    }\\n\\n} catch (Exception \\$e) {\\n    \\$response[\\\'message\\\'] = \\\'Error fetching customer statistics: \\\' . \\$e->getMessage();\\n    error_log(\\$response[\\\'message\\\']);\\n} finally {\\n    // No need to close PDO connection or statement explicitly\\n    // They will be closed automatically when the script finishes\\n}\\n\\necho json_encode(\\$response);\\n\\n// Ensure no extra output after the JSON response\\nexit();\\n?> 